mapper.select:
select
  m.id, m.ms_name, m.sex, m.age, m.phone,m.password, if(c=0 or c is null,'N','Y') cardFlag,DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%s') create_time,
       ifnull(m.point,0) point
from member m
left join (
    select ms_id,count(1) c from member_card where status = '1' group by ms_id
) t on m.id = t.ms_id

    
        and m.id = #{id}
    
    
        and m.ms_name = #{msName}
    
    
        and m.sex = #{sex}
    
    
        and m.age = #{age}
    
    
        and m.phone = #{phone}
    
    
        and m.create_time = #{createTime}
    

order by m.create_time desc
