mapper.select:
        select
          id, username, password,  name,  phone, email, img, create_time, update_time
        from user
        
            
                and id = #{id}
            
            
                and username = #{username}
            
            
                and password = #{password}
            

            
                and `name` = #{name}
            

            
                and phone = #{phone}
            
            
                and email = #{email}
            
            
                and img = #{img}
            
            
                and create_time = #{createTime}
            
            
                and update_time = #{updateTime}
